mapper.select:
select T1.ID, T1.CUSTOMER_ID, T1.CARE_DATE_START, T1.CATE_DATE_END, T1.STAFF_NAME, T1.CARE_ITEM_ID, T1.CATE_ITEM_NAME, T1.SHOP_ID, T1.SHOP_NAME, T1.PRICE_ORI, T1.PRICE_DISCUT, T1.SETTLE_TYPE, T1.PRESENT1, T1.PRESENT2, T1.PRESENTFREE, T2.USER_NAME from T1 INNER JOIN CUSTOMER T2 ON T1.CUSTOMER_ID=T2.ID where 1=1
and ( T1.STAFF_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or T2.USER_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or T1.CATE_ITEM_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and DATE_FORMAT( T1.CARE_DATE_START, '%Y-%m-%d') <= #{pd.lastEnd}
and DATE_FORMAT( T1.CARE_DATE_START, '%Y-%m-%d') >= #{pd.lastStart}

select

from 

where 1=1

	 AND CARE_DATE_START >= #{pd.start}



			  AND CARE_DATE_START <= #{pd.end}
