mapper.select:
select A.ID, A.USER_NAME, A.TYPE, A.SHOP_ID, A.SHOP_NAME, A.WECHAT_ID, A.WECHAT_FOCUS_TIME, A.MOBILE_PHONE, A.TEL, A.AGE, A.SEX, DATE_FORMAT(A.BIRTHDAY,'%Y-%m-%d') BIRTHDAY, A.ADDRESS, A.EMAIL, A.QQ, A.SKIN_COMENT, A.PHOTO, A.CARE_RATE, A.CREATE_TIME, A.MODIFY_TIME, A.CREATER, A.UPDATER, B.CADR_NAME, B.CARD_NO, B.CARD_ID, B.CUSTOMER_ID, B.CHARGE, B.SERVICE_CHAGE, B.BUY_TIME, B.BALANCE, B.SERVICE_BALANCE, B.END_TIME from A
inner left
join ( SELECT A.CADR_NAME, B.CARD_NO, B.CARD_ID, B.CUSTOMER_ID, B.CHARGE, B.SERVICE_CHAGE, B.BUY_TIME, B.BALANCE, B.SERVICE_BALANCE, B.END_TIME FROM M_CARD A inner join T_CUSTOMER_CARD B on A.ID = B.CARD_ID where 1=1
and A.ID = #{pd.CARD_ID}
) B on A.ID = B.CUSTOMER_ID where 1=1
and A.ID = #{pd.ID}
and A.TYPE = #{pd.TYPE}
and A.MOBILE_PHONE = #{pd.MOBILE_PHONE}
and A.USER_NAME LIKE CONCAT(CONCAT('%', #{pd.USER_NAME}),'%')
